function storeOptions(e){if("dist"===e){document.getElementById("store_options").innerHTML="";var t=new XMLHttpRequest;t.onreadystatechange=function(){4===t.readyState&&200===t.status&&(document.getElementById("store_options").innerHTML=t.responseText)},t.open("GET","db/stores_dist.php?q="+e,!0),t.send()}else if("abc"===e){document.getElementById("store_options").innerHTML="";var t=new XMLHttpRequest;t.onreadystatechange=function(){4===t.readyState&&200===t.status&&(document.getElementById("store_options").innerHTML=t.responseText)},t.open("GET","db/stores_abc.php?q="+e,!0),t.send()}}function storeSelected(e){if(""!==e){document.getElementById("store_info").innerHTML="";var t=new XMLHttpRequest;t.onreadystatechange=function(){4===t.readyState&&200===t.status&&(document.getElementById("store_info").innerHTML=t.responseText)},t.open("GET","db/store_info.php?q="+e,!0),t.send()}else document.getElementById("store_info").innerHTML="Please choose a store from the dropdown menu above"}